import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./App.css";

function App() {
  const [location, setLocation] = useState("");
  const [category, setCategory] = useState("all");
  const [selectedDate, setSelectedDate] = useState(null);

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div className="App">
      <h1>LocalVibe Lite</h1>
      <p>Discover local events by entering your city, category, and date</p>

      {/* Container for zip code, category, and date picker */}
      <div className="filter-container">
        <input
          type="text"
          value={location}
          onChange={handleLocationChange}
          placeholder="Enter zip code"
          className="location-input"
        />

        <select
          value={category}
          onChange={handleCategoryChange}
          className="category-dropdown"
        >
          <option value="all">All Categories</option>
          <option value="arts">Arts</option>
          <option value="music">Music</option>
          <option value="sports">Sports</option>
          <option value="tech">Technology</option>
          <option value="food">Food & Drink</option>
        </select>

        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          placeholderText="Select event date"
          className="date-picker"
          dateFormat="MMMM d, yyyy"
        />
      </div>

      {/* Polaroid-style images */}
      <div className="polaroid-container">
        <div className="polaroid">
          <img src="https://source.unsplash.com/200x200/?city" alt="city" />
          <div className="caption">City Vibes</div>
        </div>
        <div className="polaroid">
          <img src="https://source.unsplash.com/200x200/?music" alt="music" />
          <div className="caption">Music & Arts</div>
        </div>
        <div className="polaroid">
          <img src="https://source.unsplash.com/200x200/?food" alt="food" />
          <div className="caption">Food & Fun</div>
        </div>
      </div>

      <button
        onClick={() =>
          alert(
            `Searching events for: ${location} in ${category} on ${
              selectedDate ? selectedDate.toDateString() : "any date"
            }`
          )
        }
      >
        Search
      </button>
    </div>
  );
}

export default App;
