import React, { useState } from "react";

function App() {
  // Step 1: Create a state variable to hold the input value
  const [location, setLocation] = useState("");

  // Step 2: Create a function to handle input changes
  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  // Step 3: Add the input field inside the return JSX
  return (
    <div className="App">
      <h1>LocalVibe Lite</h1>
      <p>Discover local events by entering your city or zip code:</p>

      {/* Input Field for City/Zip Code */}
      <input
        type="text"
        value={location}
        onChange={handleLocationChange} // This function updates the location state
        placeholder="Enter city or zip code"
        className="location-input"
      />

      {/* For debugging purposes, display the entered location */}
      <p>Current location: {location}</p>

      {/* Add the Search Button (optional for now) */}
      <button onClick={() => alert(`Searching events for: ${location}`)}>
        Search
      </button>
    </div>
  );
}

export default App;
