import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css"; // Correct import for DatePicker CSS
import "./App.css"; // Import the updated CSS



function App() {
  // State for location input and category dropdown
  const [location, setLocation] = useState("");
  const [category, setCategory] = useState("all"); // Default category
  const [selectedDate, setSelectedDate] = useState(null); // State for date picker

  // Handle changes in location input
  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  // Handle category selection
  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  // Handle date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div className="App">
      <h1>LocalVibe Lite</h1>
      <p>Discover local events by entering your city or zip code</p>

      {/* Input Field for City/Zip Code */}
      <input
        type="text"
        value={location}
        onChange={handleLocationChange}
        placeholder="Enter city or zip code"
        className="location-input"
      />

      {/* Dropdown for selecting event category */}
      <select
        value={category}
        onChange={handleCategoryChange}
        className="category-dropdown"
      >
        <option value="all">All Categories</option>
        <option value="arts">Arts</option>
        <option value="music">Music</option>
        <option value="sports">Sports</option>
        <option value="tech">Technology</option>
        <option value="food">Food & Drink</option>
      </select>

      {/* Date picker for selecting the event date */}
      <DatePicker
        selected={selectedDate}
        onChange={handleDateChange}
        placeholderText="Select event date"
        className="date-picker"
        dateFormat="MMMM d, yyyy"
      />

      {/* Display selected category and date for debugging */}
      <p>Selected category: {category}</p>
      <p>Selected date: {selectedDate ? selectedDate.toDateString() : "None"}</p>

      {/* Search button */}
      <button
        onClick={() =>
          alert(
            `Searching events for: ${location} in ${category} on ${
              selectedDate ? selectedDate.toDateString() : "any date"
            }`
          )
        }
      >
        Search
      </button>
    </div>
  );
}

export default App;
