import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./App.css";

function App() {
  const [location, setLocation] = useState("Miami"); // Default city set to Miami
  const [category, setCategory] = useState("all");
  const [selectedDate, setSelectedDate] = useState(null);
  const [events, setEvents] = useState([]); // State to hold fetched events
  const [bookmarks, setBookmarks] = useState([]); // State to hold bookmarked events

  const TICKETMASTER_API_KEY = "yodXRWLLvt0Nlcb4Uk3hpB3RPbWi79Ac"; // Replace with your API key

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        // Fetch events from Ticketmaster API using city
        const response = await fetch(
          `https://app.ticketmaster.com/discovery/v2/events.json?apikey=${TICKETMASTER_API_KEY}&city=${location}`
        );

        if (!response.ok) {
          throw new Error(`Error: ${response.status} - ${response.statusText}`);
        }

        const data = await response.json();
        console.log("API Response:", data); // Log the entire API response to check the structure

        // Check if the data contains events
        if (data._embedded && data._embedded.events) {
          setEvents(data._embedded.events); // Update state with fetched events
        } else {
          console.log("No events found for this city.");
          setEvents([]); // Set empty array if no events are found
        }
      } catch (error) {
        console.error("Error fetching events:", error);
      }
    };

    fetchEvents();
  }, [location]); // Fetch events when the location changes

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Function to add event to bookmarks
  const handleBookmark = (event) => {
    setBookmarks((prevBookmarks) => [...prevBookmarks, event]); // Add event to bookmarks
  };

  // Function to share event details (copy to clipboard)
  const handleShare = (event) => {
    const eventDetails = `Check out this event: ${event.name}\nDate: ${new Date(
      event.dates.start.localDate
    ).toLocaleDateString()}\nLink: ${event.url}`;
    navigator.clipboard.writeText(eventDetails);
    alert("Event details copied to clipboard!");
  };

  return (
    <div className="App">
      <h1 className="main-title">LocalVibe Lite</h1>
      <p className="subtitle">
        Discover local events by entering your city, category, and date
      </p>
      {/* Filter inputs in a flexbox container */}
      <div className="filter-container">
        <input
          type="text"
          value={location}
          onChange={handleLocationChange}
          placeholder="Enter city"
          className="location-input"
        />

        <select
          value={category}
          onChange={handleCategoryChange}
          className="category-dropdown"
        >
          <option value="all">All Categories</option>
          <option value="music">Music</option>
          <option value="sports">Sports</option>
          <option value="arts">Arts</option>
          <option value="theater">Theater</option>
        </select>

        {/* Date Picker for selecting the event date */}
        <div className="date-picker-container">
          <DatePicker
            selected={selectedDate}
            onChange={handleDateChange}
            placeholderText="Select event date"
            className="date-picker"
            dateFormat="MMMM d, yyyy"
          />
        </div>
      </div>
      {/* Polaroid-style images with relevant event photos from Miami */}
      <div className="polaroid-container">
        <a
          href="https://images.pexels.com/photos/2406203/pexels-photo-2406203.jpeg"
          target="_blank"
          rel="noopener noreferrer"
          className="polaroid"
        >
          <img src="/images/miami-city.jpg" alt="City Vibes - Miami" />
          <div className="caption">City Vibes - Miami</div>
        </a>
        <a
          href="https://www.timeout.com/miami/music"
          target="_blank"
          rel="noopener noreferrer"
          className="polaroid"
        >
          <img src="/images/miami-music.jpg" alt="Miami Music & Arts" />
          <div className="caption">Miami Music & Arts</div>
        </a>
        <a
          href="https://www.miamiandbeaches.com/events/miami-spice-months"
          target="_blank"
          rel="noopener noreferrer"
          className="polaroid"
        >
          <img src="/images/miami-food.jpeg" alt="Food & Fun in Miami" />
          <div className="caption">Food & Fun in Miami</div>
        </a>
      </div>
      <button
        onClick={() =>
          alert(
            `Searching events for: ${location} in ${category} on ${
              selectedDate ? selectedDate.toDateString() : "any date"
            }`
          )
        }
      >
        Search
      </button>
      <div className="event-list">
        {events.length === 0 ? (
          <p>No events found</p>
        ) : (
          events.map((event) => (
            <div key={event.id} className="event-card">
              <h3>{event.name}</h3>
              <p>
                Date:{" "}
                {new Date(event.dates.start.localDate).toLocaleDateString()}
              </p>
              <button className="view-details-btn">
                <a href={event.url} target="_blank" rel="noopener noreferrer">
                  View Details
                </a>
              </button>
              <button onClick={() => handleBookmark(event)}>Bookmark</button>
              <button onClick={() => handleShare(event)}>Share</button>
            </div>
          ))
        )}
      </div>

      {/* Bookmarked events section */}
      <h2 className="bookmarks-title">Bookmarked Events</h2>
      <div className="bookmarked-events">
        {bookmarks.length === 0 ? (
          <p>No bookmarked events</p>
        ) : (
          bookmarks.map((event) => (
            <div key={event.id} className="event-card">
              <h3>{event.name}</h3>
              <p>
                Date:{" "}
                {new Date(event.dates.start.localDate).toLocaleDateString()}
              </p>
              <button className="view-details-btn">
                <a href={event.url} target="_blank" rel="noopener noreferrer">
                  View Details
                </a>
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
